from base64 import b64encode
from Crypto.Hash import SHA3_512
from string import printable
import random
from icecream import ic
from Crypto.Util.number import long_to_bytes


def hash(url: str):
    return SHA3_512.new(url.encode('latin1')).digest()


bad_url = "https://EVILCODE/"

while True:
    ending = ''.join(random.choices(printable, k=5))
    padded_url = bad_url + ending
    if hash(padded_url)[0] == 0:
        break

ic(padded_url)

pubkey = int('', 16)
signature_b64 = b64encode(long_to_bytes(pubkey))
print('signature:')
print(signature_b64)